import { LiqPayPay, LiqPaySubscribe } from "react-liqpay";
import React from "react";

function LiqPayButton({amount, currency, title, orderId}) {


  const ButtonComponent = () => (
    <button
      style={{
        backgroundColor: "#337ab7",
        color: "#fff",
        borderColor: "#2e6da4",
        border: "1px solid transparent",
        borderRadius: "4px",
        padding: "6px 12px",
        cursor: "pointer",
      }}
    >
      {`${payInfo.title} ${payInfo.amount} ${payInfo.currency}`}
    </button>
  );

  return (
    <LiqPayPay
      publicKey={process.env.REACT_APP_PUBLIC_KEY}
      privateKey={process.env.REACT_APP_PRIVATE_KEY}
      amount={props.amount}
      description={props.title}
      currency="UAH"
      orderId={props.orderId}
      result_url={`/orders/${props.orderId}`}
      server_url="http://server.domain.com/liqpay"
      product_description="Thank you for choosing LeSport Shop"
      style={{ margin: "8px" }}
      disabled={false}
    />
  );
}

export default LiqPayButton;
