import React from 'react'
import Pagination from 'react-bootstrap-4-pagination';
import {Link} from 'react-router-dom'
function Paginate (props) {

    let {keyword, page, pages, isAdmin} = props;
    console.log("KEYWORD split", props)

    if(keyword){
        keyword = keyword.split("?keyword="[1].split("&")[0])
        console.log("KEYWORD split", keyword)
        
    }

    let paginationConfig = {
        totalPages: pages,
        currentPage: page,
        showMax: 5,
        size: "md",
        threeDots: true,
        prevNext: true,
        href: 'https://example.com/items?page=*', // * will be replaced by the page number
        pageOneHref: 'https://example.com/items',
        
        disabledColor: 'green',
        circle: false,
        shadow: false
      };

    

    return (
pages > 1 && (
    <Pagination  {...paginationConfig}>
        {[...Array(pages).keys()].map((x)=>(
            <Link key={x+1}
            to={`/?keyword=${keyword}&page=${x+1}`}
            >

                <Pagination.Item active={x + 1=== page}>{x+1}</Pagination.Item>
            </Link>
        ))}
    </Pagination>
)
    )
}

export default Paginate
