import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { Button, Row, Col, ListGroup, Image, Card } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { PayPalButton } from 'react-paypal-button-v2';
import Message from "../components/Message";
import Loader from "../components/Loader";
import { useParams } from "react-router";

import { createOrder, getOrderDetails, payOrder } from "../actions/orderActions";

import { ORDER_CREATE_RESET } from "../constants/orderConstants";

function OrderScreen() {
 //Aaq_ZW1VOTdBTIFDQ1lRS0JRPsEztlFYlLqfiJ1gQ00qWsn_8LQrybUu16Ml_uS7mbIxiEZaoTPvJqrp
  const { id } = useParams();
  const orderId = id;

  const orderDetails = useSelector((state) => state.orderDetails);
  const { order, error, loading } = orderDetails;

  const orderPay = useSelector((state) => state.orderDetails);
  const { loading:loadingPay, success:successPay  } = orderPay;

  const [sdkReady, setSdkReady] = useState(false)

  

  const dispatch = useDispatch();
  const location = useLocation();
  const navigate = useNavigate();


  if (!loading && !error) {
    order.itemsPrice = order.orderItems
      .reduce((acc, item) => acc + item.price * item.qty, 0)
      .toFixed(2);
  }

  const addPayPalScript = () => {
    const script = document.createElement('script')
    script.type='tex/javascript';
    script.src = "https://www.paypal.com/sdk/js?client-id=Aaq_ZW1VOTdBTIFDQ1lRS0JRPsEztlFYlLqfiJ1gQ00qWsn_8LQrybUu16Ml_uS7mbIxiEZaoTPvJqrp";
    script.async = true;
    script.onload = () => {
      setSdkReady(true)
    }
    document.body.appendChild(script);
  }

  useEffect(() => {
    if (!order || successPay || order._id !== Number(orderId)) {
      dispatch(getOrderDetails(orderId));
    }else if(!order.isPaid){
      if(!window.paypal){
        addPayPalScript()
      } else {
        setSdkReady(true)
      }

    }
  }, [dispatch, order, orderId, successPay]);

  const successPaymentHandler = (paymentResult) => {
    dispatch(payOrder())
  }

  return loading ? (
    <Loader></Loader>
  ) : error ? (
    <Message variant="danger">{error}</Message>
  ) : (
    <div>
        <h1>Order: {order._id}</h1>
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Shipping</h2>
              <p><strong>Name: </strong> {order.user.name} </p>
              <p><strong>Name: </strong> <a href={`mailto:${order.user.email}`}>{order.user.email}</a> </p>
              <p>
                <strong>Shipping: </strong>
                {order.shippingAddress.address}, {order.shippingAddress.city},
                {"   "}
                {order.shippingAddress.postalCode},{"   "}
                {order.shippingAddress.country}
              </p>
              {order.isDelivered ? (
                  <Message varient="success">Delivered at {order.deliveredAt}</Message>
              ):(

                <Message varient="warning">Not delivered</Message>
              )}
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method: </strong>
                {order.paymentMethod}
              </p>
              {order.isPaid ? (
                  <Message varient="success">Paid on {order.paidAt}</Message>
              ):(

                <Message varient="warning">Not paid</Message>
              )}
            </ListGroup.Item>

            <ListGroup.Item>
              <h2>Order Items</h2>
              {order.orderItems.length === 0 ? (
                <Message variant="info">Order is empty</Message>
              ) : (
                <ListGroup variant="flush">
                  {order.orderItems.map((item, index) => (
                    <ListGroup.Item key={index}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.image}
                            alt={item.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          <Link to={`/product/${item.product}`}>
                            {item.name}{" "}
                          </Link>
                        </Col>
                        <Col md={4}>
                          {item.qty} X ${item.price} = $
                          {(item.qty * item.price).toFixed(2)}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Order Summar</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Items:</Col>

                  <Col>${order.orderPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Shipping:</Col>

                  <Col>${order.shippingPrice}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                  <Col>Tax:</Col>

                  <Col>${order.taxPrice}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                  <Col>Total:</Col>

                  <Col>${order.totalPrice}</Col>
                </Row>
              </ListGroup.Item>

              {!order.isPaid && (
                <ListGroup.Item>
                {!loadingPay&&<Loader/>}
                {!sdkReady ? (<Loader/>):(
                    <PayPalButton
                      amount={order.totalPrice}
                      onSuccess={successPaymentHandler}
                    
                    />
                )}
              </ListGroup.Item>
              )}
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default OrderScreen;
