 alert(state)
 const {products, error, landing} = productList;
  useEffect(() => {  

    dispatch(listProducts())

  }, []);


  return ( 
    <div>
      <div>
        <h1>Latest Products</h1>
        <Row>
          {products.map((product) => (
            <Col sm={12} md={6} lg={4} xl={3} key={product._id}>
