import React from 'react'
import Pagination from 'react-bootstrap-4-pagination';
import {Link, useNavigate,  useLocation} from 'react-router-dom'

function Paginate (props) {

    let {keyword, page, pages, isAdmin} = props;
    

    const location = useLocation();
    const navigate = useNavigate();
    console.log("location", location)

    if(keyword){
       let  preKeyword = location.search.split("keyword=");
       keyword = preKeyword[1].split("&");
        
       console.log("KEYWORD split", keyword)

    }
    else    
    navigate(location.pathname)
     

     

    let paginationConfig = {
        totalPages: pages,
        currentPage: page,
        showMax: pages,
        size: "md",
        threeDots: true,
        prevNext: true,
        href: `/?keyword=${keyword[0]}&page=*`, // * will be replaced by the page number
        pageOneHref: location,
        
        disabledColor: 'green',
        circle: false,
        shadow: false
      };

      console.log("paginationConfig.href", paginationConfig.href)

    

    return (
pages > 1 && (
    <Pagination  {...paginationConfig}>
       
    </Pagination>
)
    )
}

export default Paginate
